mapper.update:
UPDATE `member`
SET updateDate = NOW()

	, loginPw = #{loginPw}


	, email = #{email}


, salt = #{salt}


	, nickname = #{nickname}


	, phoneNumber = #{phoneNumber}

WHERE id = #{memberId};

UPDATE `member`
SET updateDate = NOW(),
loginPw = #{loginPw},
salt = #{salt}
WHERE id = #{id};
